import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./VirtualSessions.css";

const VirtualSessions = () => {
  const navigate = useNavigate();

  // Define available dates for sessions
  const availableDates = [
    new Date(2024, 11, 10), // December 10, 2024
    new Date(2024, 11, 12), // December 12, 2024
    new Date(2024, 11, 14), // December 14, 2024
  ];

  const [selectedDate, setSelectedDate] = useState(null);
  const [bookedDate, setBookedDate] = useState(null);

  const handleDateChange = (date) => {
    if (availableDates.some((availableDate) => availableDate.toDateString() === date.toDateString())) {
      setSelectedDate(date);
    } else {
      alert("This date is not available for booking.");
    }
  };

  const handleBookSession = () => {
    if (!selectedDate) {
      alert("Please select a valid date to book a session.");
      return;
    }
    setBookedDate(selectedDate);
    alert(`Session booked for ${selectedDate.toDateString()}`);
    setSelectedDate(null); // Clear selected date after booking
  };

  return (
    <div className="virtual-sessions-container">
      {/* Navbar */}
      <nav className="navbar">
        <h2 className="brand-name">SMHS</h2>
        <ul className="nav-links">
          <li>
            <button onClick={() => navigate("/home")} className="nav-link">Home</button>
          </li>
          <li>
            <button onClick={() => navigate("/one-on-one-sessions")} className="nav-link">One-on-One Sessions</button>
          </li>
          <li>
            <button onClick={() => navigate("/virtual-sessions")} className="nav-link">Virtual Sessions</button>
          </li>
          <li>
            <button onClick={() => navigate("/about-us")} className="nav-link">About Us</button>
          </li>
          <li>
            <button onClick={() => navigate("/login")} className="logout-button">Logout</button>
          </li>
        </ul>
      </nav>

      {/* Header */}
      <header className="hero-section">
        <h1>Virtual Sessions</h1>
        <p>Select an available date to book your session.</p>
      </header>

      {/* Booking Calendar Section */}
      <section className="calendar-section">
        <h2>Book Your Session</h2>
        <Calendar
          onChange={handleDateChange}
          value={selectedDate}
          tileClassName={({ date }) =>
            availableDates.some((availableDate) => availableDate.toDateString() === date.toDateString())
              ? "available-date"
              : null
          }
        />
        {selectedDate && (
          <div className="selected-date">
            <p>Selected Date: {selectedDate.toDateString()}</p>
            <button onClick={handleBookSession} className="book-button">
              Book Session
            </button>
          </div>
        )}
        {bookedDate && <p className="booked-message">Session booked for: {bookedDate.toDateString()}</p>}
      </section>

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 SMHS. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default VirtualSessions;
