import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { DateRangePicker } from "react-date-range";
import "react-date-range/dist/styles.css"; // Main CSS file
import "react-date-range/dist/theme/default.css"; // Theme CSS file
import "./VirtualSessions.css";

const VirtualSessions = () => {
  const navigate = useNavigate();
  const [selectedSession, setSelectedSession] = useState(null);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [selectedDates, setSelectedDates] = useState(null);
  const [bookingMessage, setBookingMessage] = useState("");

  // Limited session dates
  const disabledDates = [
    new Date(2024, 11, 12),
    new Date(2024, 11, 14),
    new Date(2024, 11, 16),
    new Date(2024, 11, 18),
  ];

  const videos = [
    {
      id: "bNtLd73CQPU",
      title: "Anxiety Management",
      link: "https://www.youtube.com/watch?v=bNtLd73CQPU",
    },
    {
      id: "eGVWRvNe1-A",
      title: "Stress Reduction Techniques",
      link: "https://www.youtube.com/watch?v=eGVWRvNe1-A",
    },
    {
      id: "Gljq2FHzTvY",
      title: "Overcoming Depression",
      link: "https://www.youtube.com/watch?v=Gljq2FHzTvY",
    },
  ];

  const liveSessions = [
    { id: 1, title: "Anxiety Management Live Session", counselor: "Dr. Jane Smith" },
    { id: 2, title: "Stress Reduction Live Session", counselor: "Dr. Emily Davis" },
    { id: 3, title: "Overcoming Depression Live Session", counselor: "Dr. Robert Johnson" },
  ];

  const handleBookSession = (session) => {
    setSelectedSession(session);
    setShowDatePicker(true);
  };

  const handleDateSelection = (ranges) => {
    setSelectedDates(ranges.selection);
  };

  const confirmBooking = () => {
    if (selectedDates && selectedDates.startDate && selectedDates.endDate) {
      setBookingMessage(
        `Session '${selectedSession.title}' booked from ${selectedDates.startDate.toDateString()} to ${selectedDates.endDate.toDateString()}`
      );
      setShowDatePicker(false);
      setSelectedSession(null);
    } else {
      alert("Please select a date range.");
    }
  };

  return (
    <div className="virtual-sessions-container">
      {/* Navbar */}
      <nav className="navbar">
        <h2 className="brand-name">SMHS</h2>
        <ul className="nav-links">
          <li>
            <button onClick={() => navigate("/home")} className="nav-link">Home</button>
          </li>
          <li>
            <button onClick={() => navigate("/one-on-one-sessions")} className="nav-link">One-on-One Sessions</button>
          </li>
          <li>
            <button onClick={() => navigate("/virtual-sessions")} className="nav-link">Virtual Sessions</button>
          </li>
          <li>
            <button onClick={() => navigate("/about-us")} className="nav-link">About Us</button>
          </li>
          <li>
            <button onClick={() => navigate("/login")} className="logout-button">Logout</button>
          </li>
        </ul>
      </nav>

      {/* Header */}
      <header className="hero-section">
        <h1>Virtual Sessions</h1>
        <p>Explore virtual counseling sessions and watch therapy videos for mental health support.</p>
      </header>

      {/* Therapy Videos Section */}
      <section className="therapy-videos-section">
        <h2>Therapy Videos</h2>
        <div className="video-grid">
          {videos.map((video) => (
            <div
              className="video-card"
              key={video.id}
              onClick={() => window.open(video.link, "_blank")}
            >
              <img
                src={`https://img.youtube.com/vi/${video.id}/0.jpg`}
                alt={video.title}
              />
              <p>{video.title}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Live Session Booking Section */}
      <section className="live-sessions-section">
        <h2>Live Sessions</h2>
        <div className="sessions-list">
          {liveSessions.map((session) => (
            <div className="session-card" key={session.id}>
              <h3>{session.title}</h3>
              <p>{`With ${session.counselor}`}</p>
              <button className="book-button" onClick={() => handleBookSession(session)}>
                Book Now
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* Date Picker */}
      {showDatePicker && (
        <div className="date-picker-container">
          <h3>Select Dates for '{selectedSession.title}'</h3>
          <DateRangePicker
            ranges={[
              {
                startDate: new Date(),
                endDate: new Date(),
                key: "selection",
              },
            ]}
            onChange={handleDateSelection}
            disabledDates={disabledDates}
            moveRangeOnFirstSelection={false}
            editableDateInputs
          />
          <button className="confirm-button" onClick={confirmBooking}>
            Confirm Booking
          </button>
        </div>
      )}

      {/* Booking Confirmation Message */}
      {bookingMessage && (
        <div className="booking-confirmation">
          <p>{bookingMessage}</p>
        </div>
      )}

      {/* Footer */}
      <footer className="footer">
        <p>&copy; 2024 SMHS. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default VirtualSessions;
